import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

const api = axios.create({
    baseURL: API_URL,
    headers: {
        'Content-Type': 'application/json'
    },
    timeout: 5000,
    withCredentials: false
});

// Add request interceptor with better error handling
api.interceptors.request.use(
    (config) => {
        console.log('Making request to:', config.url);
        const token = localStorage.getItem('token');
        if (token) {
            config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
    },
    (error) => {
        console.error('Request interceptor error:', error);
        return Promise.reject(error);
    }
);

// Add response interceptor with better error handling
api.interceptors.response.use(
    (response) => {
        console.log('Received response from:', response.config.url);
        return response;
    },
    (error) => {
        console.error('API Error:', {
            url: error.config?.url,
            method: error.config?.method,
            status: error.response?.status,
            data: error.response?.data
        });

        if (error.response) {
            // The request was made and the server responded with a status code
            // that falls out of the range of 2xx
            console.error('Response error:', {
                status: error.response.status,
                data: error.response.data
            });
            throw error.response.data.error || 'An error occurred';
        } else if (error.request) {
            // The request was made but no response was received
            console.error('No response received:', error.request);
            throw 'Unable to connect to the server. Please check if the server is running.';
        } else {
            // Something happened in setting up the request that triggered an Error
            console.error('Request setup error:', error.message);
            throw error.message;
        }
    }
);

export const login = async (email, password) => {
    try {
        console.log('Attempting login with:', { email });
        const response = await api.post('/auth/login', { email, password });
        console.log('Login response:', response.data);
        localStorage.setItem('token', response.data.token);
        return response.data;
    } catch (error) {
        console.error('Login error:', error);
        throw error;
    }
};

export const signup = async (userData) => {
    try {
        console.log('Attempting signup with:', { ...userData, password: '[REDACTED]' });
        const response = await api.post('/auth/signup', userData);
        console.log('Signup successful:', { userId: response.data.user.id });
        localStorage.setItem('token', response.data.token);
        return response.data;
    } catch (error) {
        console.error('Signup error:', error);
        if (error.response?.data?.error) {
            throw new Error(error.response.data.error);
        } else if (error.response?.status === 500) {
            throw new Error('Server error. Please try again later.');
        } else if (!error.response) {
            throw new Error('Network error. Please check your connection.');
        }
        throw new Error('An unexpected error occurred during registration.');
    }
};

export const submitQuestionnaire = async (profileData) => {
    try {
        console.log('Submitting questionnaire data:', profileData);
        const response = await api.post('/user/preferences', profileData);
        console.log('Questionnaire response:', response.data);
        return response.data;
    } catch (error) {
        console.error('Questionnaire submission error:', error);
        throw error;
    }
};

export const getUserProfile = async () => {
    try {
        const response = await api.get('/user/profile');
        return response.data;
    } catch (error) {
        throw error.response?.data?.error || 'An error occurred';
    }
};

export const logout = () => {
    localStorage.removeItem('token');
};

export default api;
