import React, { useState, useEffect } from 'react';
import './UserProfile.css';

const UserProfile = () => {
    const [userPreferences, setUserPreferences] = useState(null);
    const [userStats, setUserStats] = useState({
        totalSessions: 0,
        averageAccuracy: 0,
        favoriteYogaPose: '',
        lastPractice: ''
    });

    useEffect(() => {
        // Load user preferences from localStorage
        const preferences = JSON.parse(localStorage.getItem('userPreferences'));
        setUserPreferences(preferences);

        // In a real app, you would fetch these stats from your backend
        // This is just mock data for demonstration
        setUserStats({
            totalSessions: 12,
            averageAccuracy: 78,
            favoriteYogaPose: 'Tree Pose',
            lastPractice: '2024-02-20'
        });
    }, []);

    if (!userPreferences) {
        return <div>Loading...</div>;
    }

    return (
        <div className="user-profile">
            <div className="profile-header">
                <h1>User Profile</h1>
                <p>Welcome back, {userPreferences.name || 'Yogi'}!</p>
            </div>

            <div className="stats-grid">
                <div className="stat-card">
                    <i className="fas fa-clock"></i>
                    <h3>Total Sessions</h3>
                    <p>{userStats.totalSessions}</p>
                </div>
                <div className="stat-card">
                    <i className="fas fa-bullseye"></i>
                    <h3>Average Accuracy</h3>
                    <p>{userStats.averageAccuracy}%</p>
                </div>
                <div className="stat-card">
                    <i className="fas fa-star"></i>
                    <h3>Favorite Pose</h3>
                    <p>{userStats.favoriteYogaPose}</p>
                </div>
                <div className="stat-card">
                    <i className="fas fa-calendar"></i>
                    <h3>Last Practice</h3>
                    <p>{userStats.lastPractice}</p>
                </div>
            </div>

            <div className="preferences-section">
                <h2>Your Preferences</h2>
                <div className="preferences-grid">
                    <div className="preference-item">
                        <label>Experience Level</label>
                        <p>{userPreferences.experienceLevel}</p>
                    </div>
                    <div className="preference-item">
                        <label>Practice Duration</label>
                        <p>{userPreferences.practiceDuration} minutes</p>
                    </div>
                    <div className="preference-item">
                        <label>Focus Areas</label>
                        <p>{userPreferences.focusAreas?.join(', ')}</p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default UserProfile;
