import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { login, signup } from '../services/api';
import './Login.css';

function Login({ onLogin }) {
    const navigate = useNavigate();
    const [isSignUp, setIsSignUp] = useState(false);
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    const [formData, setFormData] = useState({
        email: '',
        password: '',
        userProfile: {
            age: '',
            weight: '',
            height: '',
            experience: 'beginner',
            poseCount: '4'
        }
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name.includes('.')) {
            const [parent, child] = name.split('.');
            setFormData(prev => ({
                ...prev,
                [parent]: {
                    ...prev[parent],
                    [child]: value
                }
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setIsLoading(true);

        try {
            let response;
            if (isSignUp) {
                response = await signup(formData);
            } else {
                response = await login(formData.email, formData.password);
            }

            onLogin(response.user);
            navigate('/yoga');
        } catch (err) {
            setError(err.toString());
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="login-container">
            <div className="login-box">
                <div className="login-content">
                    <div className="login-header">
                        <h2>{isSignUp ? 'Create Account' : 'Welcome Back'}</h2>
                        <p className="login-subtitle">
                            {isSignUp
                                ? 'Start your yoga journey today'
                                : 'Continue your yoga practice'}
                        </p>
                    </div>

                    <form onSubmit={handleSubmit} className="login-form">
                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                                required
                                placeholder="Enter your email"
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="password">Password</label>
                            <input
                                type="password"
                                id="password"
                                name="password"
                                value={formData.password}
                                onChange={handleChange}
                                required
                                placeholder="Enter your password"
                                minLength="6"
                            />
                        </div>

                        {isSignUp && (
                            <>
                                <div className="form-group">
                                    <label htmlFor="age">Age</label>
                                    <input
                                        type="number"
                                        id="age"
                                        name="userProfile.age"
                                        value={formData.userProfile.age}
                                        onChange={handleChange}
                                        required
                                        min="13"
                                        max="100"
                                        placeholder="Enter your age"
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="weight">Weight (kg)</label>
                                    <input
                                        type="number"
                                        id="weight"
                                        name="userProfile.weight"
                                        value={formData.userProfile.weight}
                                        onChange={handleChange}
                                        required
                                        min="30"
                                        max="200"
                                        placeholder="Enter your weight in kg"
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="height">Height (cm)</label>
                                    <input
                                        type="number"
                                        id="height"
                                        name="userProfile.height"
                                        value={formData.userProfile.height}
                                        onChange={handleChange}
                                        required
                                        min="100"
                                        max="250"
                                        placeholder="Enter your height in cm"
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="experience">Yoga Experience</label>
                                    <select
                                        id="experience"
                                        name="userProfile.experience"
                                        value={formData.userProfile.experience}
                                        onChange={handleChange}
                                        required
                                    >
                                        <option value="beginner">Beginner</option>
                                        <option value="intermediate">Intermediate</option>
                                        <option value="advanced">Advanced</option>
                                    </select>
                                </div>

                                <div className="form-group">
                                    <label htmlFor="poseCount">Number of Poses</label>
                                    <select
                                        id="poseCount"
                                        name="userProfile.poseCount"
                                        value={formData.userProfile.poseCount}
                                        onChange={handleChange}
                                        required
                                    >
                                        {[1, 2, 3, 4, 5, 6, 7, 8].map(num => (
                                            <option key={num} value={num}>
                                                {num} {num === 1 ? 'pose' : 'poses'}
                                            </option>
                                        ))}
                                    </select>
                                </div>
                            </>
                        )}

                        {error && <div className="error-message">{error}</div>}

                        <button type="submit" className="submit-button" disabled={isLoading}>
                            {isLoading
                                ? 'Please wait...'
                                : (isSignUp ? 'Sign Up' : 'Sign In')}
                        </button>

                        <p className="toggle-form">
                            {isSignUp
                                ? 'Already have an account? '
                                : "Don't have an account? "}
                            <button
                                type="button"
                                className="toggle-button"
                                onClick={() => {
                                    setIsSignUp(!isSignUp);
                                    setError('');
                                }}
                            >
                                {isSignUp ? 'Sign In' : 'Sign Up'}
                            </button>
                        </p>
                    </form>
                </div>

                <div className="vertical-divider"></div>

                <div className="login-info">
                    <h3>Why Choose MindMaxxing?</h3>
                    <p>
                        Join our community and transform your yoga practice with cutting-edge AI technology.
                    </p>
                    <ul className="benefits-list">
                        <li>Personalized yoga sequences based on your level</li>
                        <li>Real-time pose correction and feedback</li>
                        <li>Track your progress and improvement</li>
                        <li>Access to a variety of yoga styles</li>
                        <li>Practice at your own pace, anytime</li>
                    </ul>
                    <p>
                        Our AI-powered platform adapts to your needs, helping you achieve your yoga goals safely and effectively.
                    </p>
                </div>
            </div>
        </div>
    );
}

export default Login;
