import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './UserQuestionnaire.css';

function UserQuestionnaire({ onSubmit }) {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        age: '',
        weight: '',
        height: '',
        experience: 'beginner',
        poseCount: '4'
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onSubmit(formData);
        navigate('/yoga');
    };

    return (
        <div className="questionnaire-container">
            <div className="questionnaire-box">
                <h2>Tell Us About Yourself</h2>
                <p className="questionnaire-subtitle">
                    Help us personalize your yoga experience
                </p>

                <form onSubmit={handleSubmit} className="questionnaire-form">
                    <div className="form-group">
                        <label htmlFor="age">Age</label>
                        <input
                            type="number"
                            id="age"
                            name="age"
                            value={formData.age}
                            onChange={handleChange}
                            required
                            min="13"
                            max="100"
                            placeholder="Enter your age"
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="weight">Weight (kg)</label>
                        <input
                            type="number"
                            id="weight"
                            name="weight"
                            value={formData.weight}
                            onChange={handleChange}
                            required
                            min="30"
                            max="200"
                            placeholder="Enter your weight in kg"
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="height">Height (cm)</label>
                        <input
                            type="number"
                            id="height"
                            name="height"
                            value={formData.height}
                            onChange={handleChange}
                            required
                            min="100"
                            max="250"
                            placeholder="Enter your height in cm"
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="experience">Yoga Experience</label>
                        <select
                            id="experience"
                            name="experience"
                            value={formData.experience}
                            onChange={handleChange}
                            required
                        >
                            <option value="beginner">Beginner</option>
                            <option value="intermediate">Intermediate</option>
                            <option value="advanced">Advanced</option>
                        </select>
                    </div>

                    <div className="form-group">
                        <label htmlFor="poseCount">Number of Poses</label>
                        <select
                            id="poseCount"
                            name="poseCount"
                            value={formData.poseCount}
                            onChange={handleChange}
                            required
                        >
                            {[1, 2, 3, 4, 5, 6, 7, 8].map(num => (
                                <option key={num} value={num}>
                                    {num} {num === 1 ? 'pose' : 'poses'}
                                </option>
                            ))}
                        </select>
                    </div>

                    <button type="submit" className="submit-button">
                        Start Your Practice
                    </button>
                </form>
            </div>
        </div>
    );
}

export default UserQuestionnaire;
